import React, { useState } from "react";

function CreateArea() {
  // Get the input title and content
  // Pass them to App.jsx

  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  function handleTitle(event) {
    const input = event.target.value;
    setNote((prev) => {
      return {
        title: input,
        content: prev.content,
      };
    });
  }

  function handleContent(event) {
    const input = event.target.value;
    setNote((prev) => {
      return {
        title: prev.content,
        content: input,
      };
    });
  }
  return (
    <div>
      <form>
        <input onChange={handleTitle} name="title" placeholder="Title" />
        <textarea onChange={handleTitle} name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
