  });

  function handleTitle(event) {
    console.log(event.target.value);
    setNote((prev)=>{
      return {
        title: "",
    content: "",
      }
    });
  }

  function handleContent(event) {
    console.log(event.target.value);
  }
  return (
    <div>
      <form>
        <input name="title" placeholder="Title" />
        <textarea name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

